mapper.select:
	SELECT * FROM (
	    SELECT tb.*, rownum rNum from (
	        SELECT * FROM diary	
            order by diary_idx desc
	    ) Tb
	)
	where rNum>=#{start}
			and rNum<=#{end}

mapper.update:
UPDATE diary SET
ofile=#{ofile}, sfile=#{sfile}, description=#{description}, 
temperature=#{temperature}, humidity=#{humidity}, sunlight=#{sunlight}
WHERE diary_idx=#{diary_idx}

mapper.select:
SELECT * FROM diary
WHERE DATE_FORMAT(postdate, '%Y') = #{year}
  AND DATE_FORMAT(postdate, '%m') = #{month}
